import { Component, OnInit } from '@angular/core';
import {MatButtonModule} from '@angular/material/button';
import {MatCardModule} from '@angular/material/card';
import { HttpClient } from '@angular/common/http';
import { HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-matchup-card',
  templateUrl: './matchup-card.component.html',
  standalone: true,
  imports: [ MatCardModule, MatButtonModule ],
  styleUrls: ['./matchup-card.component.css']
})
export class MatchupCardComponent implements OnInit {
  newdata:any;

  constructor(private http: HttpClient) { }

  ngOnInit() {
	this.getData();
  }

  getData() {
	 // this.http.get('http://127.0.0.1:5000/topGame').map(response => response.json()).subscribe(data => this.newdata = data);
    this.http.get('http://127.0.0.1:5000/gameCard').subscribe(data => {
      console.log(data)
      this.newdata = data;//JSON.stringify(data);
      });
  }
}
<mat-card class="example-card" *ngFor="let game of games">
    <mat-card-header>
      <mat-card-title>{{game?.matchup}}</mat-card-title>
      <mat-card-subtitle>{{game?.conferences}}</mat-card-subtitle>
    </mat-card-header>
    <mat-card-content>
        <p>Venue: {{game?.venue}}</p>
        <p>Line Provider: {{game?.provider}}</p>
        <p>Current Spread: {{newdata?.currSpread}}</p>
        <p>Opening Spread: {{newdata?.openSpread}}</p>
        <p>Current Total: {{newdata?.currTotal}}</p>
        <p>Opening Total: {{newdata?.openTotal}}</p>
    </mat-card-content>
    <mat-card-actions class="example-action">
      <button mat-button>FULL PREVIEW</button>
    </mat-card-actions>
  </mat-card>
