import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { NgFor } from '@angular/common';
import { HttpClientModule } from '@angular/common/http';
import { MatGridListModule } from '@angular/material/grid-list';
import { MatchupCardComponent } from '../matchup-card/matchup-card.component'

@Component({
  selector: 'app-card-grid',
  standalone: true,
  imports: [ 
    HttpClientModule,
    MatchupCardComponent,
    MatGridListModule, 
    NgFor
  ],
  templateUrl: './card-grid.component.html',
  styleUrls: ['./card-grid.component.css']
})
export class CardGridComponent implements OnInit {
  numGames:any;

  constructor(private http: HttpClient) { }

  ngOnInit() {
	this.newdata = this.getData();
  //numGames = this.newdata;
  }

  tiles = [
    {text: 'One', cols: 3, rows: 1, color: 'lightblue'},
    {text: 'Two', cols: 1, rows: 2, color: 'lightgreen'},
    {text: 'Three', cols: 1, rows: 1, color: 'lightpink'},
    {text: 'Four', cols: 2, rows: 1, color: '#DDBDF1'},
  ];

  getData() {
    // this.http.get('http://127.0.0.1:5000/topGame').map(response => response.json()).subscribe(data => this.newdata = data);
     this.http.get('http://127.0.0.1:5000/numGames').subscribe(data => {
       console.log(data)
       this.newdata = data;//JSON.stringify(data);
       });
   }
}
